package Lec1;

public class Odd_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int n = 894;
     if(n%2 == 0) {
    	 
    	 System.out.println("Even Number");
     }
     else {
    	 System.out.println("Odd Number");
     }
	}
   // If bina else ke chal jata hai...else nai chal skta bina else ke
	// Multiple if use kar skte hai but not multiple else...multiple if & else likh skte hai. Multiple if likh ke ek else likh skte hai..
}
