package Lec15;

public class String_Demo_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str1 = "Hello";
		System.out.println(str1 + 10 + 30); // normally as a string concatinate hoga 
		System.out.println(str1 + (10 + 30)); // pehle bracket solve hoga uske baad concatinate hoga
		
		// string + anything is a string
	}

}
