package Lecture_2;
import java.util.*;
public class Pattern0 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    Scanner sc = new Scanner(System.in);
    int n = sc.nextInt();
    int i = 1; //initialisation
    while(i <= n) { //condition
    	System.out.print("*"+" ");
    	i++; //re-initialisation
    }
	}

}
